import { CLEAR_CART, DECREASE, INCREASE, REMOVE } from '../actions';

const Reducer = (state, action) => {
	switch(action.type){
		case CLEAR_CART:
			return { ...state, cart: [] };
		case DECREASE:
			const cartDec = state.cart(cartItem => {
				return { ...cartItem, qty: action.payload.qty - 1 }
			});
			return {
				...state,
				cart: cartDec
			}
		case INCREASE:
			const cartInc = state.cart.map( cartItem => {
				if(cartItem.id === action.payload.id) {
					return { ...cartItem, qty: action.payload.qty + 1 }
				}
				return cartItem;
			});
			return {
				...state,
				cart: cartInc
				}
		case REMOVE:
			return {
				...state, 
				cart: state.cart.filter(cartItem => cartItem.id !== action.payload.id)
			};
		default:
			return state;
	}
}

export default Reducer;
