import { CLEAR_CART, DECREASE, INCREASE, REMOVE } from '../actions';

const Reducer = (state, action) => {
	switch(action.type){
		case CLEAR_CART:
			return { ...state, cart: [] };
		case DECREASE:
			let cartArrayDec = [];
			if(action.payload.qty === 1) {
				cartArrayDec = state.cart.filter(cartItem => cartItem.id !== action.payload.id)
			} else {
				cartArrayDec = state.cart.map(cartItem => {
					if(action.payload.id === cartItem.id) {
						return { ...cartItem, qty: action.payload.qty - 1 }
					}
					return cartItem;
				});
			}

			return {
				...state,
				cart: cartArrayDec
			}

		case INCREASE:
			const cartArrayInc = state.cart.map( cartItem => {
				if(cartItem.id === action.payload.id) {
					return { ...cartItem, qty: action.payload.qty + 1 }
				}
				return cartItem;
			});
			return {
				...state,
				cart: cartArrayInc
				}

		case REMOVE:
			return {
				...state, 
				cart: state.cart.filter(cartItem => cartItem.id !== action.payload.id)
			};
		default:
			return state;
	}
}

export default Reducer;
